import javax.swing.*;import java.awt.*;import java.util.*;public class Game{static int s=0,a=-1;static JLabel l;public static void main(String[]args){JFrame f=new JFrame("Game");f.setSize(400,500);f.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);f.setLayout(new BorderLayout());l=new JLabel("Score:0",SwingConstants.CENTER);l.setFont(new Font("Arial",Font.BOLD,20));f.add(l,BorderLayout.NORTH);JPanel p=new JPanel(new GridLayout(3,3));JButton[]bs=new JButton[9];for(int i=0;i<9;i++){bs[i]=new JButton();bs[i].setBackground(Color.LIGHT_GRAY);bs[i].setOpaque(true);bs[i].setBorder(BorderFactory.createLineBorder(Color.BLACK,2));int j=i;bs[i].addActionListener(e->{if(j==a){s++;l.setText("Score:"+s);bs[a].setBackground(Color.LIGHT_GRAY);a=-1;}else g(f);});p.add(bs[i]);}f.add(p,BorderLayout.CENTER);f.setFocusable(true);f.requestFocusInWindow();f.setVisible(true);java.util.Timer t=new java.util.Timer();Random r=new Random();t.schedule(new TimerTask(){public void run(){if(a!=-1){bs[a].setBackground(Color.LIGHT_GRAY);g(f);}a=r.nextInt(9);bs[a].setBackground(Color.GREEN);t.schedule(new TimerTask(){public void run(){if(a!=-1){bs[a].setBackground(Color.LIGHT_GRAY);g(f);}}},3000);}},0,2000+r.nextInt(3000));}static void g(JFrame f){JOptionPane.showMessageDialog(f,"Game Over! Score:"+s);f.dispose();System.exit(0);}}
